def bin_octet(bin):
    return [int(bin[i:i+8], 2) for i in range(0, len(bin), 8)]

def bin_to_hex(bin):
    return hex(int(bin, 2))[2:]

def hex_bin(hex):
    return bin(int(hex, 16))[2:]
    
